{
	uint64_t expected = 0x03;
	uint64_t actual = 0;
	char *str = "(1 << 0) | (1 << 1)";
	int result = parse(str, strlen(str), &actual);

	cr_assert_eq(actual, expected, "%" PRIu64 " == %" PRIu64, actual,
		     expected);
	cr_assert_eq(result, 0);
}

